const INITIAL_USUARIO = {
  page: 'Revisão de REDUX',
  email: '',
  password: '',
}

function reducerUsuario(state = INITIAL_USUARIO, action) {
  switch (action.type.upperase()) {
    case 'EMAIL':
      return { ...state, email: action.value };
    case 'PASSWORD':
      return { ...state, email: action.value };
    default:
      return state;
  }
}

export default reducerUsuario;
